import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [profilePicture, setProfilePicture] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate(); // Hook för att navigera programatiskt

  useEffect(() => {
    fetch('/api/profile-picture', { credentials: 'include' })
      .then((response) => response.json())
      .then((data) => {
        if (data.profile_picture) {
          setProfilePicture(data.profile_picture);
          setIsLoggedIn(true);
        } else {
          setIsLoggedIn(false);
        }
      })
      .catch((error) => {
        console.error('Error fetching profile picture:', error);
        setIsLoggedIn(false);
      });
  }, []);

  // Funktion för att hantera inloggningsknappen
  const handleLoginClick = () => {
    navigate('/login'); // Omdirigerar användaren till /login
  };

  return (
    <nav className='navbar'>
      <div className='navbar-container'>
        <Link to="/" className="navbar-logo">Resonate</Link>
        <div className='navbar-menu'>
          <Link to="/" className="navbar-item">Home</Link>
          <Link to="/discovery" className="navbar-item">Discovery</Link>
          {isLoggedIn ? (
            <Link to="/profile" className="navbar-item">Profile</Link>
          ) : (
            <button className="navbar-item login-btn" onClick={handleLoginClick}>
              Log in
            </button>
          )}
        </div>
        {isLoggedIn && (
          <div className='navbar-profile'>
            <img
              src={profilePicture}
              alt='Profile'
              className='navbar-profile-pic'
            />
          </div>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
